export default async function handler(req, res) {
  const GOOGLE_API_HOST = "https://generativelanguage.googleapis.com";
  const url = new URL(req.url, `http://${req.headers.host}`);
  const targetUrl = `${GOOGLE_API_HOST}${url.pathname}${url.search}`;
  try {
    const forwardHeaders = {};
    for (const [key, value] of Object.entries(req.headers)) {
      if (!["host", "connection", "x-forwarded-for", "x-vercel-id", "x-real-ip", "accept-encoding"].includes(key.toLowerCase())) {
        forwardHeaders[key] = value;
      }
    }
    let body = null;
    if (req.method !== "GET" && req.method !== "HEAD") {
      const chunks = [];
      for await (const chunk of req) { chunks.push(chunk); }
      body = Buffer.concat(chunks);
    }
    const response = await fetch(targetUrl, {
      method: req.method,
      headers: forwardHeaders,
      body: body,
    });
    for (const [key, value] of response.headers.entries()) {
      if (!["transfer-encoding", "connection", "content-encoding"].includes(key.toLowerCase())) {
        res.setHeader(key, value);
      }
    }
    const responseBody = await response.text();
    res.status(response.status).send(responseBody);
  } catch (error) {
    res.status(502).json({ error: "Proxy error", message: error.message });
  }
}

